
function CameraController(){this.x=0;this.y=0;this.width=GameController.nbCol*GameController.cell;this.height=GameController.nbRow*GameController.cell;var a=this;MessageBus.getInstance().listen(MessageBus.MSG_TYPES.characterHasMoved,function(b){a.centerAt.call(a,b)})}CameraController.prototype={centerAt:function(a){this.x=a.x-floor((GameController.nbCol-1)/2);this.y=a.y-floor((GameController.nbRow-1)/2)}};